import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';

//import { Cliente } from '../models/cliente';
import { Transaccion } from '../models/transaccion.model';


@Injectable({
  providedIn: 'root'
})
export class ClienteService {
  private apiUrl = 'http://localhost:8082/clientes';

  constructor(private http: HttpClient) { }

  buscarClientePorNumeroIdentificacion(numeroIdentificacion: string): Observable<any> {
    return this.http.get<any>(`${this.apiUrl}/buscar/${numeroIdentificacion}`);
  }

  agregarCliente(cliente: any): Observable<any> {
    return this.http.post<any>(this.apiUrl, cliente);
  }

  /*obtenerFondosDisponibles(): Observable<any[]> {
    return this.http.get<any[]>(`${this.apiUrl}/fondos`);

  }*/

  asociarFondos(clienteId: string, fondosSuscritos: any[]): Observable<any> {
    return this.http.post<any>(`${this.apiUrl}/${clienteId}/fondos`, fondosSuscritos);
  }

  realizarTransaccion(clienteId: string, transaccion: Transaccion): Observable<any> {
    return this.http.post<any>(`${this.apiUrl}/${clienteId}/transacciones`, transaccion);
  }

  getCliente(clienteId: string): Observable<Cliente> {
    const url = `${this.apiUrl}/clientes/${clienteId}`;
    return this.http.get<Cliente>(url);
  }

}
