import { Component, OnInit } from '@angular/core';
import { ProductService } from '../services/product.service'; // Importa el servicio de productos
import { OrderService } from '../services/order.service'; // Importa el servicio de órdenes
import { Product } from '../models/product.model'; // Importa el modelo de productos
import { Order } from '../models/order.model'; // Importa el modelo de órdenes

@Component({
  selector: 'app-orders',
  templateUrl: './orders.component.html',
  styleUrls: ['./orders.component.css']
})

@Component({
  selector: 'app-orders',
  templateUrl: './orders.component.html',
  styleUrls: ['./orders.component.css']
})
export class OrdersComponent implements OnInit {
  orders: Order[] = [];
  newOrder: Order = { userId: '', deliveryAddresss: '', paymentMethod: '', total: 0, date: new Date(), status: '', orderDetails: [] }; // Define una nueva orden
  selectedProducts: { [productId: stringnumber]: boolean } = {}; // Define un objeto para almacenar los productos seleccionados

  products: Product[] = []; // Define un arreglo de productos

  constructor(private orderService: OrderService, private productService: ProductService) {}

  ngOnInit(): void {
    this.loadOrders();
    this.loadProducts();
  }

  loadOrders(): void {
    this.orderService.getOrders().subscribe(data => {
      this.orders = data;
    });
  }

  loadProducts(): void {
    this.productService.getProducts().subscribe(data => {
      this.products = data;
    });
  }

  addOrder(): void {
    this.newOrder.orderDetails = Object.keys(this.selectedProducts)
      .filter(productId => this.selectedProducts[productId])
      .map(productId => ({ product: this.products.find(product => product._id === productId)!, quantity: 1 }));

    this.orderService.createOrder(this.newOrder).subscribe(() => {
      this.loadOrders();
      this.newOrder = { userId: '', deliveryAddresss: '', paymentMethod: '', total: 0, date: new Date(), status: '', orderDetails: [] };
      this.selectedProducts = {};
    });
  }

  updateOrder(id: number): void {
    this.newOrder.total = this.calculateTotal(this.newOrder.orderDetails);
    this.orderService.updateOrder(id, this.newOrder).subscribe(() => {
      this.loadOrders();
    });
  }

  deleteOrder(id: number): void {
    this.orderService.deleteOrder(id).subscribe(() => {
      this.loadOrders();
    });
  }

  editOrder(order: Order): void {
    this.newOrder = { ...order };
  }

  calculateTotal(items: OrderItem[]): number {
    return items.reduce((total, item) => total + item.price * item.quantity, 0);
  }
}

<div>
    <h2>Órdenes</h2>
    <ul>
      <li *ngFor="let order of orders">
        <div>
          <h3>Orden #{{ order._id }}</h3>
          <p>Cliente ID: {{ order.customerId }}</p>
          <p>Dirección: {{ order.address }}</p>
          <p>Fecha: {{ order.date | date }}</p>
          <p>Estado: {{ order.status }}</p>
          <p>Método de Pago: {{ order.paymentMethod }}</p>
          <p>Total: {{ order.total }}</p>
          <ul>
            <li *ngFor="let item of order.items">
              {{ item.quantity }}x {{ item.product.name }} - {{ item.product.price }}$
            </li>
          </ul>
          <button (click)="deleteOrder(order._id)">Eliminar</button>
          <button (click)="editOrder(order)">Editar</button>
        </div>
      </li>
    </ul>
    <!-- Formulario para agregar órdenes -->
    <form (ngSubmit)="addOrder()">
      <input [(ngModel)]="newOrder.userId" name="customerId" placeholder="ID del Cliente" required />
      <input [(ngModel)]="newOrder.deliveryAddresss" name="address" placeholder="Dirección" required />
      <input [(ngModel)]="newOrder.paymentMethod" name="paymentMethod" placeholder="Método de Pago" required />
      <input [(ngModel)]="newOrder.total" name="total" placeholder="Total" required />
      <input [(ngModel)]="newOrder.date" name="date" type="date" placeholder="Fecha" required />
      <input [(ngModel)]="newOrder.status" name="status" placeholder="Estado" required />
      <div *ngFor="let product of products">
        <label>
          <input type="checkbox" [(ngModel)]="selectedProducts[product._id]" [value]="product._id" /> {{ product.name }} - {{ product.price }}$
        </label>
      </div>
      <button type="submit">Agregar Orden</button>
    </form>
  </div>
