import { Component, OnInit } from '@angular/core';
import { ProductService } from '../services/product.service';
import { Product, Image } from '../models/product.model';

@Component({
  selector: 'app-products',
  templateUrl: './products.component.html',
  styleUrls: ['./products.component.css']
})
export class ProductsComponent implements OnInit {
  products: Product[] = [];
  newProduct: Product = {
    _id: 0, // Inicialmente en 0, se debe asignar en el backend
    name: '',
    description: '',
    price: 0,
    quantity: 0,
    images: [],
    created_at: new Date(),
    updated_at: new Date()
  };
  selectedProduct: Product | null = null;

  constructor(private productService: ProductService) { }

  ngOnInit(): void {
    this.loadProducts();
  }

  loadProducts(): void {
    this.productService.getProducts().subscribe(products => {
      this.products = products;
    });
  }

  addProduct(): void {
    this.productService.createProduct(this.newProduct).subscribe(product => {
      this.products.push(product);
      this.resetNewProduct();
    });
  }

  updateProduct(): void {
    if (this.selectedProduct) {
      this.productService.updateProduct(this.selectedProduct).subscribe(updatedProduct => {
        const index = this.products.findIndex(p => p._id === updatedProduct._id);
        if (index !== -1) {
          this.products[index] = updatedProduct;
        }
        this.selectedProduct = null;
      });
    }
  }

  deleteProduct(productId: number): void {
    this.productService.deleteProduct(productId).subscribe(() => {
      this.products = this.products.filter(p => p._id !== productId);
    });
  }

  selectProduct(product: Product): void {
    this.selectedProduct = { ...product };
  }

  resetNewProduct(): void {
    this.newProduct = {
      _id: 0,
      name: '',
      description: '',
      price: 0,
      quantity: 0,
      images: [],
      created_at: new Date(),
      updated_at: new Date()
    };
  }

  addImage(path: string): void {
    const image: Image = {
      id: 0, // Inicialmente en 0, se debe asignar en el backend
      path,
      product_id: this.selectedProduct ? this.selectedProduct._id : this.newProduct._id,
      created_at: new Date(),
      updated_at: new Date()
    };
    if (this.selectedProduct) {
      this.selectedProduct.images.push(image);
    } else {
      this.newProduct.images.push(image);
    }
  }

  removeImage(index: number): void {
    if (this.selectedProduct) {
      this.selectedProduct.images.splice(index, 1);
    } else {
      this.newProduct.images.splice(index, 1);
    }
  }
// Funci칩n para reemplazar 'public' por 'storage' en las rutas de las im치genes
  replacePublicWithStorage(imagePath: string): string {
  return imagePath.replace('public', 'storage');
}

}
