// src/app/create-order/create-order.component.ts
import { Component, OnInit } from '@angular/core';
import { FormArray, FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Product } from '../models/product.model';
import { OrderService } from '../services/order.service';
import { ProductService } from '../services/product.service';

@Component({
  selector: 'app-create-order',
  templateUrl: './create-order.component.html',
  styleUrls: ['./create-order.component.css']
})
export class CreateOrderComponent implements OnInit {
  orderForm: FormGroup;
  products: Product[] = [];

  constructor(
    private fb: FormBuilder,
    private orderService: OrderService,
    private productService: ProductService
  ) {
    this.orderForm = this.fb.group({
      date: ['', Validators.required],
      status: ['', Validators.required],
      delivery_address: ['', Validators.required],
      payment_method: ['', Validators.required],
      user_id: ['', Validators.required],
      orderDetails: this.fb.array([])
    });
  }

  ngOnInit() {
    this.productService.getProducts().subscribe(data => {
      this.products = data;
    });
  }

  get orderDetails() {
    return this.orderForm.get('orderDetails') as FormArray;
  }

  addProduct() {
    const orderDetailGroup = this.fb.group({
      product_id: ['', Validators.required],
      quantity: ['', [Validators.required, Validators.min(1)]]
    });
    this.orderDetails.push(orderDetailGroup);
  }

  removeProduct(index: number) {
    this.orderDetails.removeAt(index);
  }

  getProduct(productId: number): Product | undefined {
    //return this.products.find(product => product.id === productId);
    return this.productService.getProductById(productId);
  }

  getProductById(productId: number): Observable<Product> {
    return this.http.get<Product>(`${this.apiUrl}/${productId}`);
  }

  /*onSubmit() {
    if (this.orderForm.valid) {
      const orderData: { order: Order; orderDetails: OrderItem[] } = { // Ajusta el tipo de orderData y orderDetails     
        order: {
          date: this.orderForm.value.date,
          status: this.orderForm.value.status,
          deliveryAddress: this.orderForm.value.delivery_address,
          paymentMethod: this.orderForm.value.payment_method,
          userId: this.orderForm.value.user_id
        },
        orderDetails: this.orderForm.value.orderDetails.map((detail: any) => ({
          productId: detail.product_id,
          quantity: detail.quantity
        }))
      };

      this.orderService.createOrder(orderData).subscribe(response => {
        console.log('Order created', response);
      });
    }
  }*/

  onSubmit() {
    if (this.orderForm.valid) {
      const orderData = {
        order: {
          date: this.orderForm.value.date,
          status: this.orderForm.value.status,
          deliveryAddress: this.orderForm.value.delivery_address,
          paymentMethod: this.orderForm.value.payment_method,
          userId: this.orderForm.value.user_id
        },
        orderDetails: this.orderForm.value.orderDetails.map((detail: any) => ({
          productId: detail.product_id,
          quantity: detail.quantity
        }))
      };

      const jsonString = JSON.stringify(orderData);
      console.log(jsonString); // Imprime el JSON generado en la consola

      this.orderService.createOrder(jsonString).subscribe(response => {
        console.log('Order created', response);
      });

      // Aquí puedes enviar el JSON al servicio OrderService si es necesario
    }
  }

}
