import { Component, OnInit } from '@angular/core';
import { ProductService } from '../services/product.service';
import { Product, Image } from '../models/product.model';

@Component({
  selector: 'app-products',
  templateUrl: './products.component.html',
  styleUrls: ['./products.component.css']
})
export class ProductsComponent implements OnInit {
  products: Product[] = [];
  newProduct: Product = {
    _id: 0, // Inicialmente en 0, se debe asignar en el backend
    name: '',
    description: '',
    price: 0,
    quantity: 0,
    images: [],
    created_at: new Date(),
    updated_at: new Date()
  };
  selectedProduct: Product | null = null;

  selectedImage: string | ArrayBuffer | null = null;

  selectedImagePreviews: string[] = [];


  constructor(private productService: ProductService) { }

  ngOnInit(): void {
    this.loadProducts();
  }

  loadProducts(): void {
    this.productService.getProducts().subscribe(products => {
      this.products = products;
    });
  }

  addProduct(): void {
    const images: Image[] = [];
    this.productService.createProduct(this.newProduct).subscribe(product => {
      product.images = images;
      this.products.push(product);
      this.resetNewProduct();

    });
  }

  updateProduct(): void {
    if (this.selectedProduct) {
      this.productService.updateProduct(this.selectedProduct).subscribe(updatedProduct => {
        const index = this.products.findIndex(p => p._id === updatedProduct._id);
        if (index !== -1) {
          this.products[index] = updatedProduct;
        }
        this.selectedProduct = null;
      });
    }
  }

  deleteProduct(productId: number): void {
    this.productService.deleteProduct(productId).subscribe(() => {
      this.products = this.products.filter(p => p._id !== productId);
    });
  }

  selectProduct(product: Product): void {
    this.selectedProduct = { ...product };
  }

  resetNewProduct(): void {
    this.newProduct = {
      _id: 0,
      name: '',
      description: '',
      price: 0,
      quantity: 0,
      images: [],
      created_at: new Date(),
      updated_at: new Date()
    };
  }

  addImage(path: string): void {
    const image: Image = {
      id: 0, // Inicialmente en 0, se debe asignar en el backend
      path,
      product_id: this.selectedProduct ? this.selectedProduct._id : this.newProduct._id,
      created_at: new Date(),
      updated_at: new Date()
    };
    if (this.selectedProduct) {
      this.selectedProduct.images.push(image);
    } else {
      this.newProduct.images.push(image);
    }
  }

  removeImage(index: number): void {
    if (this.selectedProduct) {
      this.selectedProduct.images.splice(index, 1);
    } else {
      this.newProduct.images.splice(index, 1);
    }
  }
// Función para reemplazar 'public' por 'storage' en las rutas de las imágenes
  replacePublicWithStorage(imagePath: string): string {
    return imagePath.replace('public', 'http://localhost:8000/storage');
  }

  onFileSelected(event: any) {
    const file: File = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = () => {
      this.selectedImage = reader.result;
    };
    reader.readAsDataURL(file);
    }
  }

  onFilesSelected(event: any): void {
    const files: FileList = event.target.files;
    if (files) {
      this.newProduct.images = [];
      this.selectedImagePreviews = [];
      for (let i = 0; i < files.length; i++) {
        const file: File = files[i];
        // Crear la imagen y asociarla al producto nuevo
        this.newProduct.images.push({ id: i, path: file.name, product_id: this.newProduct._id, created_at: new Date(), updated_at: new Date() });
        // Crear una URL para la vista previa de la imagen y agregarla al arreglo
        const imageUrl = URL.createObjectURL(file);
        this.selectedImagePreviews.push(imageUrl);
      }
    }
  }



}
